/** Replace with template. `I'm ${name}` + { name: 'bamboo' } = I'm bamboo */
export default function replaceMessage(template, kv) {
  if (typeof template === 'undefined') {
    return '';
  }

  return template.replace(/\${\w+}/g, function (str) {
    var key = str.slice(2, -1);
    return kv[key];
  });
}